import { mapGetters } from 'vuex'
import axios from 'axios'

export default {
  name: 'Dashboard',
  data() {
    return {
      tableData: [], // 存放用户列表
      // 选择楼层内容
      value: '',
      // 选择开始时间和结束时间
      startTime: '',
      endTime: '',
      // 单选
      radio: '', // 存放被选择序号
      send_radio: '' // 存放被选择的内容
    }
  },
  computed: {
    ...mapGetters(['name'])
  },
  mounted() {
    this.findTrackList()
  },
  methods: {
    // 获取轨迹列表
    findTrackList: function() {
      // console.log('12345678')
      axios.post('http://112.74.189.126:8080/bluefire/Integererface/data_manage/getOutdoorUsers', {})
        .then(response => {
          console.log('成功获取轨迹列表')
          this.tableData = [response.data.content.list[1]] // 返回的轨迹列表与tableData绑定
        },
        err => {
          console.log(err)
          console.log('未成功获取轨迹列表')
        })
      // console.log('987654321')
    },
    // 单选函数
    showRow: function(row) {
      this.radio = this.tableData.indexOf(row)
      this.send_radio = row.userId
    },
    toTrackList: function() {
      if (this.send_radio === '') {
        this.$message({
          message: '尚未选择用户',
          type: 'warning'
        })
      } else {
        this.$router.push({
          path: '/wenyu/list',
          query: {
            userId: this.send_radio
          }
        })
      }
    }
  }
}
