import { mapGetters } from 'vuex'
import axios from 'axios'

export default {
  name: 'Dashboard',
  data() {
    return {
      userlist: [], // 存放用户列表
      // 选择楼层内容
      value: '',
      // 选择开始时间和结束时间
      startTime: '',
      endTime: '',
      // 轨迹列表内容存放处
      // 单选
      userId: '',
      tableData: [],
      radio: '', // 存放被选择序号
      send_radio: '' // 存放被选择的内容
    }
  },
  computed: {
    ...mapGetters(['name'])
  },
  mounted() {
    this.showTrack()
  },
  methods: {
    showTrack: function() {
      this.userId = this.$route.query.userId
      axios
        .post(
          'http://112.74.189.126:8080/bluefire/Integererface/data_manage/getOutdoorTrajectories',
          { 'userId': this.userId }
        )
        .then(
          response => {
            this.tableData = response.data.content.list
            console.log('获得选择的轨迹信息，然后调转到地图界面并显示轨迹')
          },
          err => {
            console.log('没有轨迹信息')
          }
        )
    },
    showRow: function(row) {
      this.radio = this.tableData.indexOf(row)
      this.send_radio = row.trajectoryId
    },
    passTrack: function() {
      if (this.send_radio === '') {
        this.$message({
          message: '尚未选择路线',
          type: 'warning'
        })
      } else {
        this.$router.push({
          path: '/wenyu/showTrack',
          query: {
            trajectoryId: this.send_radio
          }
        })
      }
    }
  }
}
